import React, { useState } from "react";
import "./comments.css";

const Comments = ({ comments, handleAddComments, handleCommentDelete }) => {
  const [showInput, setShowInput] = useState(false);
  const [commentBody, setCommentBody] = useState("");

  const handleAdd = () => {
    handleAddComments(comments.id, commentBody);
    setShowInput(false);
    setCommentBody("");
  };

  return (
    <div>
      {comments.map((comment) => (
        <div key={comment.id} className={`${comment.content && "comment-container"}`}>
          <h3>{comment.content}</h3>
          {showInput && (
            <input
              type="text"
              value={commentBody}
              onChange={(e) => setCommentBody(e.target.value)}
              autoFocus
            />
          )}
          {showInput ? (
            <div>
              <button onClick={handleAdd}>Add</button>
              <button onClick={() => setShowInput(false)}>Cancel</button>
            </div>
          ) : (
            comment.content && (
              <div>
                <button onClick={() => setShowInput(true)}>Reply</button>
                <button onClick={() => handleCommentDelete(comment.id)}>Delete</button>
              </div>
            )
          )}
           <div style={{ paddingLeft: "30px" }}>
        {comments?.replies?.map((com) => (
          <Comments
            key={com.id}
            comments={com}
            handleAddComments={handleAddComments}
            handleCommentDelete={handleCommentDelete}
          />
        ))}
      </div>
        </div>
      ))}
    </div>
  );
};

export default Comments;
