import './comments.css'

const Comments = ({comments}) => {
  return (
    <div>
      <div className="comment-container">
        <h3>{comments.text}</h3>
        <div>
          <button>Reply</button>
          <button>Delete</button>
        </div>
      </div>
      <div>
       {
        comments
       }
      </div>
    </div>
  );
};

export default Comments;
