import React from 'react'
import Comments from './components/comments/comments'

const useFunction = () => {

    const addComment =(tree, commentId, newComment)=>{

        if(tree.id === commentId){
            tree.replies.unShift(newComment);
            return 
        }
    }
  return {addComment}
}

export default useFunction
