import { useState } from "react";
import "./comments.css";

const Comments = ({ comments, handleAddComments }) => {
  const [showInput, setShowInput] = useState(false);

  const [commentBody, setCommentBody] = useState("")

  const handleAdd = ()=>{
    let newComments ={
      id: Date.now(),
      text: commentBody,
      replies:[]
    }
    handleAddComments(comments.id, newComments);
  }

  return (
    <div>
      <div className="comment-container">
        <h3>{comments.text}</h3>
        {showInput && <input
         type="text"
         onChange={(e)=> setCommentBody(e.target.value)}
          autoFocus />}
        {showInput ? (
          <div>
            <button>Add</button>
            <button onClick={()=>setShowInput(false)}>Cancle</button>
          </div>
        ) : (
          <div>
            <button onClick={() => setShowInput(true)}>Reply</button>
            <button>Delete</button>
          </div>
        )}
      </div>
      <div style={{ paddingLeft: "30px" }}>
        {comments?.replies?.map((com) => (
          <Comments key={com.id} comments={com} handleAddComments={handleAddComments}/>
        ))}
      </div>
    </div>
  );
};

export default Comments;
